import React, { useEffect } from 'react';
import './Carousel.css';
import experienceGif from './gifs/rowing.gif';
import speakingGif from './gifs/speaking.gif';

const carouselItems = [
  {
    title: "Experience",
    description: "Marshall Scholar at Cambridge",
    gif: experienceGif,
    link: "https://www.cai.cam.ac.uk/news/engineering-putting-people-first"
  },
  {
    title: "Music",
    description: "Classical Pianist",
    gif: experienceGif,
    link: "https://youtu.be/l4OjamOr9Pk?si=oVyNEZa7LQAjiZf9"
  },
  {
    title: "Athletics",
    description: "Rowing & Sports",
    gif: experienceGif,
    link: "https://youtu.be/9j-zqBvEcPU?si=X3_YPUt8_rtRaA61"
  },
  {
    title: "Community",
    description: "Discussing human dignity in a secular world at the 2021 de Nicola Conference at the University of Notre Dame",
    gif: speakingGif,
    link: "https://youtu.be/nfU3aCVCwYM?si=h1UKJD6MraRYEXF6"
  },
];

function Carousel() {
    return (
      <section className="carousel-section">
        <div className="carousel-track-container">
          <div className="carousel-track">
            {[...carouselItems, ...carouselItems].map((item, index) => (
              <a 
                key={index}
                href={item.link}
                className="carousel-item"
                target={item.link.startsWith('http') ? '_blank' : undefined}
                rel={item.link.startsWith('http') ? 'noopener noreferrer' : undefined}
                onClick={(e) => {
                  if (item.link.startsWith('#')) {
                    e.preventDefault();
                    document.querySelector(item.link).scrollIntoView({ 
                      behavior: 'smooth' 
                    });
                  }
                }}
              >
                <div className="carousel-image">
                  <img src={item.gif} alt={item.title} />
                </div>
                <h3>{item.title}</h3>
                <p>{item.description}</p>
              </a>
            ))}
          </div>
        </div>
      </section>
    );
}

export default Carousel;
