import React, { useEffect } from 'react';
import './Carousel.css';

const carouselItems = [
  {
    title: "Experience",
    description: "Marshall Scholar at Cambridge",
    icon: "ðŸŽ“",
    link: "https://www.cai.cam.ac.uk/news/engineering-putting-people-first"
  },
  {
    title: "Skills",
    description: "Robotics & Automation",
    icon: "ðŸ¤–",
    link: "https://www.linkedin.com/in/hannahgillespie/"
  },
  {
    title: "Research",
    description: "Autonomous Systems",
    icon: "ðŸ”¬",
    link: "https://www.cam.ac.uk/research"
  },
  {
    title: "Projects",
    description: "Full-stack Development",
    icon: "ðŸ’»",
    link: "https://www.cam.ac.uk/"
  }
];

function Carousel() {
    return (
      <section className="carousel-section">
        <div className="carousel-track-container">
          <div className="carousel-track">
            {[...carouselItems, ...carouselItems].map((item, index) => (
              <a 
                key={index}
                href={item.link}
                className="carousel-item"
                target={item.link.startsWith('http') ? '_blank' : undefined}
                rel={item.link.startsWith('http') ? 'noopener noreferrer' : undefined}
                onClick={(e) => {
                  if (item.link.startsWith('#')) {
                    e.preventDefault();
                    document.querySelector(item.link).scrollIntoView({ 
                      behavior: 'smooth' 
                    });
                  }
                }}
              >
                <div className="carousel-icon">{item.icon}</div>
                <h3>{item.title}</h3>
                <p>{item.description}</p>
              </a>
            ))}
          </div>
        </div>
      </section>
    );
  }

export default Carousel;
