import React, { useEffect } from 'react';
import './Carousel.css';
import experienceGif from './gifs/rowing.gif';
import speakingGif from './gifs/speaking.gif';

const carouselItems = [
  {
    title: "Marshall Scholar",
    description: "Awarded the 2023 Marshall Scholarship for study in the UK",
    gif: experienceGif,
    link: "https://www.cai.cam.ac.uk/news/engineering-putting-people-first",
    buttons: [
      { text: "Notre Dame Press Release", url: "https://news.nd.edu/news/alumna-hannah-gillespie-named-2023-marshall-scholar/" },
      { text: "Forbes Feature", url: "https://www.forbes.com/sites/michaeltnietzel/2022/12/14/the-2023-class-of-marshall-scholars-has-been-selected/" },
      { text: "Marshall Profile", url: "https://www.marshallscholarship.org/scholars/scholar-profiles/hannah-gillespie/" },
      { text: "Cambridge Feature", url: "https://www.cai.cam.ac.uk/news/engineering-putting-people-first" }
    ]
  },
  {
    title: "Music",
    description: "Classical Pianist",
    gif: experienceGif,
    link: "https://youtu.be/l4OjamOr9Pk?si=oVyNEZa7LQAjiZf9",
    buttons: [
      { text: "Cambridge", url: "https://www.cam.ac.uk/" },
      { text: "Marshall", url: "https://www.marshallscholarship.org/" },
      { text: "Research", url: "https://www.cai.cam.ac.uk/research" }
    ]
  },
  {
    title: "Athletics",
    description: "Rowing & Sports",
    gif: experienceGif,
    link: "https://youtu.be/9j-zqBvEcPU?si=X3_YPUt8_rtRaA61",
    buttons: [
      { text: "Cambridge", url: "https://www.cam.ac.uk/" },
      { text: "Marshall", url: "https://www.marshallscholarship.org/" },
      { text: "Research", url: "https://www.cai.cam.ac.uk/research" }
    ]
  },
  {
    title: "Community",
    description: "Speaking on the relationship between community and human dignity in a secular world",
    gif: speakingGif,
    link: "https://youtu.be/nfU3aCVCwYM?si=h1UKJD6MraRYEXF6",
    buttons: [
      { text: "Cambridge", url: "https://www.cam.ac.uk/" },
      { text: "Marshall", url: "https://www.marshallscholarship.org/" },
      { text: "Research", url: "https://www.cai.cam.ac.uk/research" }
    ]
  },
];

function CarouselItem({ item, index }) {
  const handleParentClick = (e) => {
    // If clicking on a button, prevent the parent link from activating
    if (e.target.closest('.carousel-tag-button')) {
      e.preventDefault();
    }
  };

  return (
    <a 
      href={item.link}
      className="carousel-item"
      target="_blank"
      rel="noopener noreferrer"
      onClick={handleParentClick}
    >
      <div className="carousel-image">
        <img src={item.gif} alt={item.title} />
      </div>
      <h3>{item.title}</h3>
      <p>{item.description}</p>
      <div className="carousel-buttons">
        {item.buttons?.map((button, btnIndex) => (
          <a
            key={btnIndex}
            href={button.url}
            className="carousel-tag-button"
            target="_blank"
            rel="noopener noreferrer"
            onClick={(e) => {
              e.stopPropagation();
            }}
          >
            {button.text}
          </a>
        ))}
      </div>
    </a>
  );
}

function Carousel() {
  return (
    <section className="carousel-section">
      <div className="carousel-track-container">
        <div className="carousel-track">
          {[...carouselItems, ...carouselItems].map((item, index) => (
            <CarouselItem key={index} item={item} index={index} />
          ))}
        </div>
      </div>
    </section>
  );
}

export default Carousel;
